import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Chart } from 'react-chartjs-2';
import 'chart.js/auto';

const Summary = ({ selectedCountry }) => {
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        let url = 'http://localhost:3000/';
        if (selectedCountry) {
          url += `specificcountryinfo/${selectedCountry}`;
        } else {
          url += 'cases';
        }

        const response = await axios.get(url);
        setSummary(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchSummary();
  }, [selectedCountry]);

  return (
    <div className="my-4">
      <h2 className="mb-4 text-center">Summary</h2>
      {summary ? (
        <div className="row">
          {selectedCountry ? (
            <div className="col-md-6">
              <ul className="list-group">
                <li className="list-group-item">
                  <strong>Country:</strong> {selectedCountry}
                </li>
                <li className="list-group-item">
                  <strong>Total Cases:</strong> {summary.cases}
                </li>
                <li className="list-group-item">
                  <strong>Total Deaths:</strong> {summary.deaths}
                </li>
                <li className="list-group-item">
                  <strong>Today's Cases:</strong> {summary.todayCases}
                </li>
                <li className="list-group-item">
                  <strong>Today's Deaths:</strong> {summary.todayDeaths}
                </li>
              </ul>
            </div>
          ) : (
            <div className="col-md-6">
              <ul className="list-group">
                <li className="list-group-item">
                  <strong>Total Cases:</strong> {summary.cases}
                </li>
              </ul>
            </div>
          )}
          <div className="col-md-6">
  <div className="chart-container height-200">
    <Chart type="pie" data={getChartData(summary)} />
  </div>
</div>
        </div>
      ) : (
        <p>Loading summary...</p>
      )}
    </div>
  );
};

const getChartData = (summary) => {
  const chartData = {
    labels: ['Cases', 'Deaths', "Today's Cases", "Today's Deaths"],
    datasets: [
      {
        data: [
          summary.cases,
          summary.deaths,
          summary.todayCases,
          summary.todayDeaths,
        ],
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#FF9F40',
        ],
        hoverBackgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#FF9F40',
        ],
      },
    ],
  };
  return chartData;
};

export default Summary;
